import{_ as e,c as d,o,ae as c}from"./chunks/framework.CB6Kt8Tg.js";const m=JSON.parse('{"title":"Keyboard shortcuts","description":"","frontmatter":{},"headers":[],"relativePath":"getting-started/keyboard-shortcuts.md","filePath":"getting-started/keyboard-shortcuts.md","lastUpdated":1740603064000}'),r={name:"getting-started/keyboard-shortcuts.md"};function a(i,t,h,n,l,s){return o(),d("div",null,t[0]||(t[0]=[c('<h1 id="keyboard-shortcuts" tabindex="-1">Keyboard shortcuts <a class="header-anchor" href="#keyboard-shortcuts" aria-label="Permalink to &quot;Keyboard shortcuts&quot;">​</a></h1><p>Most of the Keyboard Shortcuts in Glyphr Studio are focused on pages where you edit glyphs - namely Characters, Ligatures, and Components.</p><h3 id="project-actions" tabindex="-1">Project actions <a class="header-anchor" href="#project-actions" aria-label="Permalink to &quot;Project actions&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Key combination</th><th>Description</th></tr></thead><tbody><tr><td><code>Ctrl</code> <code>S</code></td><td>Save a Glyphr Studio Project File (.gs2)</td></tr><tr><td><code>Ctrl</code> <code>E</code></td><td>Export an OTF Font (.otf)</td></tr><tr><td><code>Ctrl</code> <code>G</code></td><td>Export an SVG Font (.svg)</td></tr><tr><td><code>Ctrl</code> <code>O</code></td><td>Open a project in a new tab</td></tr></tbody></table><h3 id="selecting-and-navigating" tabindex="-1">Selecting and navigating <a class="header-anchor" href="#selecting-and-navigating" aria-label="Permalink to &quot;Selecting and navigating&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Key combination</th><th>Description</th></tr></thead><tbody><tr><td><code>Ctrl</code> <code>.</code></td><td>Navigate to the next Item (Character, Ligature, Component, or Kern Group)</td></tr><tr><td><code>Ctrl</code> <code>,</code></td><td>Navigate to the previous Item (Character, Ligature, Component, or Kern Group)</td></tr><tr><td><code>Ctrl</code> <code>A</code></td><td>Resize (Arrow) tool: Select all shapes</td></tr><tr><td><code>Ctrl</code> <code>A</code></td><td>Path Edit (Pen) tool: Select all path points</td></tr><tr><td><code>Ctrl</code> <code>Shift</code> <code>A</code></td><td>Clear all shape and path point selections</td></tr><tr><td><code>]</code></td><td>Select next shape</td></tr><tr><td><code>Shift</code> <code>]</code></td><td>Add the next shape to the selection (multi-select)</td></tr><tr><td><code>[</code></td><td>Select previous shape</td></tr><tr><td><code>Shift</code> <code>[</code></td><td>Add the previous shape to the selection (multi-select)</td></tr><tr><td><code>Ctrl</code> <em><code>Click a shape</code></em></td><td>[Resize (Arrow) Tool - hovering over a shape] Toggle selection for that shape (multi-select)<br>Either on the edit canvas, or the layers panel</td></tr><tr><td><code>Ctrl</code> <code>➝</code></td><td>Select the next Path Point</td></tr><tr><td><code>Ctrl</code> <code>Shift</code> <code>➝</code></td><td>Add the next Path Point to the selection (multi-select)</td></tr><tr><td><code>Ctrl</code> <code>⭠</code></td><td>Select the previous Path Point</td></tr><tr><td><code>Ctrl</code> <code>Shift</code> <code>⭠</code></td><td>Add the previous Path Point to the selection (multi-select)</td></tr><tr><td><code>Ctrl</code> <em><code>Click a path point</code></em></td><td>[Path Edit (Pen) Tool - hovering over a path point] Toggle selection for that path point (multi-select)</td></tr><tr><td><code>Esc</code></td><td>Close all dialogs</td></tr></tbody></table><h3 id="view" tabindex="-1">View <a class="header-anchor" href="#view" aria-label="Permalink to &quot;View&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Key combination</th><th>Description</th></tr></thead><tbody><tr><td><code>Space</code> or <code>Scroll wheel click</code></td><td>Toggle the pan tool</td></tr><tr><td><code>Ctrl</code> <code>Scroll wheel</code></td><td>Zoom in and out</td></tr><tr><td><code>Ctrl</code> <code>+</code></td><td>Zoom in</td></tr><tr><td><code>Ctrl</code> <code>-</code></td><td>Zoom out</td></tr><tr><td><code>Ctrl</code> <code>0</code></td><td>Auto-fit glyph on the screen</td></tr><tr><td><code>Ctrl</code> <code>Space</code></td><td>Toggle distraction free preview mode</td></tr></tbody></table><h3 id="editing" tabindex="-1">Editing <a class="header-anchor" href="#editing" aria-label="Permalink to &quot;Editing&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Key combination</th><th>Description</th></tr></thead><tbody><tr><td><code>Ctrl</code> <code>C</code></td><td>Copy the selected shapes</td></tr><tr><td><code>Ctrl</code> <code>V</code></td><td>Paste the selected shapes (Glyphr Studio Clipboard)<br>Paste to import copied SVG Code (Operating System Clipboard)</td></tr><tr><td><code>Ctrl</code> <code>]</code></td><td>Move shape up</td></tr><tr><td><code>Ctrl</code> <code>[</code></td><td>Move shape down</td></tr><tr><td><code>Ctrl</code> <code>Shift</code> <code>]</code></td><td>Move shape to the top</td></tr><tr><td><code>Ctrl</code> <code>Shift</code> <code>[</code></td><td>Move shape to the bottom</td></tr><tr><td><code>Delete</code> or <code>Backspace</code></td><td>Delete the selected shapes or path points</td></tr><tr><td><code>⭠</code> <code>⭡</code> <code>➝</code> <code>⭣</code></td><td>Nudge the selected shape or path point <span class="number">1em</span><br>Press <code>Shift</code> to nudge <span class="number">10em</span></td></tr><tr><td><code>Shift</code> <em><code>Resize shape</code></em></td><td>[Resize (Arrow) Tool] Maintain aspect ratio</td></tr><tr><td><code>Shift</code> <em><code>Drag shape</code></em></td><td>[Resize (Arrow) Tool - hovering over a shape] Snap shape movement to horizontal or vertical</td></tr><tr><td><code>Shift</code> <em><code>Drag path point</code></em></td><td>[Path Edit (Pen) Tool - hovering over a path point] Snap point movement to horizontal or vertical</td></tr><tr><td><code>Shift</code> <em><code>Drag handle</code></em></td><td>[Path Edit (Pen) Tool - hovering over a handle] Maintain original handle angle</td></tr><tr><td><code>Ctrl</code> <em><code>Shift</code></em> <code>Drag handle</code></td><td>[Path Edit (Pen) Tool - hovering over a handle] Snap handle movement to horizontal or vertical</td></tr><tr><td><code>Shift</code> <em><code>Shape Rotation handle</code></em></td><td>[Shape Rotate Handle] Snap rotation degrees to whole numbers</td></tr><tr><td><code>Ctrl</code> <code>R</code></td><td>Round values for the current selection<br>(Whole Shapes for the Resize tool, Path Points + Handles for the Path Edit tool)</td></tr><tr><td><code>Shift</code> <em><code>Click</code></em></td><td>[New Path Tool] Snap the new point&#39;s coordinates to whole numbers</td></tr><tr><td><code>Shift</code> <em><code>Click</code></em></td><td>[Path Add Point Tool] Snap the new point&#39;s coordinates to whole numbers</td></tr><tr><td><code>Ctrl</code> <em><code>Click</code></em></td><td>[Path Add Point Tool] Add the new point as a corner point with hidden handles</td></tr></tbody></table><h3 id="tools" tabindex="-1">Tools <a class="header-anchor" href="#tools" aria-label="Permalink to &quot;Tools&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Key combination</th><th>Description</th></tr></thead><tbody><tr><td><code>B</code> or <code>P</code></td><td>Select the Path Edit (Pen) tool</td></tr><tr><td><code>V</code> or <code>A</code></td><td>Select the Resize (Arrow) tool</td></tr><tr><td><code>M</code> or <code>R</code></td><td>Select the New Rectangle tool</td></tr><tr><td><code>O</code> or <code>Q</code></td><td>Select the New Oval tool</td></tr><tr><td><code>H</code> or <code>W</code></td><td>Select the New Path tool</td></tr><tr><td><code>U</code> or <code>E</code></td><td>Select the Path Add Point tool</td></tr></tbody></table>',12)]))}const b=e(r,[["render",a]]);export{m as __pageData,b as default};
